import React from "react";
import { useState } from "react";

import { Document, Page, pdfjs   } from "react-pdf";
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;


function PdfViewer() {
    const [numOfPages,setNumOfPages] = useState(null);
    const [pageNumber,setPageNumber] = useState(1);


    const pdfFiles = ['./Screen Shot 2022-05-13 at 12.40.05 PM.pdf','./Screen Shot 2022-05-13 at 12.40.13 PM.pdf','./Screen Shot 2022-05-13 at 12.40.18 PM.pdf']

    const [files, setFile] = useState(pdfFiles);

    const changePage = (offset) => {
        setPageNumber(prevPageNumber => prevPageNumber + offset);
      }

    function onDocumentLoadSuccess({numOfPages}){
        setNumOfPages(numOfPages);
        setPageNumber(1);

    }

    const previousPage = () => { changePage(-1); }

    const nextPage = () => { 
        console.log("hi");
        changePage(+1); }



    const options = {
        cMapUrl: 'cmaps/',
        cMapPacked: true,
        standardFontDataUrl: 'standard_fonts/'
      };

    return(
        <div>
            <Document file={files[0]} onLoadSuccess={onDocumentLoadSuccess} options={options}>
            <Page pageNumber={pageNumber} />
            </Document>
            <p>
            <button disabled={pageNumber <= 1} onClick={previousPage} type="button" >Previous Page</button>
                <button disabled={pageNumber >= numOfPages} onClick={nextPage} type="button">Next Page</button>
                <br/>

      </p>

        </div>
    )



}

export default PdfViewer;
