import React from "react";
import { useState } from "react";

import { Document, Page, pdfjs   } from "react-pdf";
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;


function PdfViewer() {
    const [numOfPages,setNumOfPages] = useState(null);
    const [pageNumber,setPageNumber] = useState(1);

    const [file, setFile] = useState('./Screen Shot 2022-05-13 at 12.40.05 PM.pdf');

    function onDocumentLoadSuccess({numOfPages}){
        setNumOfPages(numOfPages);

    }
    const options = {
        cMapUrl: 'cmaps/',
        cMapPacked: true,
        standardFontDataUrl: 'standard_fonts/',
        workerSrc: '/Users/b002055/Documents/musicApp/my-app/node_modules/pdfjs-dist/build/pdf.worker.js'

      };

    return(
        <div>
            <Document file={file} onLoadSuccess={onDocumentLoadSuccess} options={options}>
            <Page pageNumber={pageNumber} />
            </Document>
            <p>
        Page {pageNumber} of {numOfPages}
      </p>

        </div>
    )



}

export default PdfViewer;
